import React, { useEffect, useState } from "react";
import Axios from "axios";
import { Link } from "react-router-dom";

function Dashboard({ location }) {
  const [money, setBalance] = useState("");
  const [username, setUsername] = useState("");
  const [id, setID] = useState(location.state.id);

  useEffect(() => {
    Axios.get(`http://localhost:5000/get/${id}`).then((response) => {
      setUsername(response.data[0].first_name);
      setBalance(response.data[0].money);
    });
  }, [id]);

  return (
    <div>
      <h1>Dashboard</h1>
      <p>Welcome {username}!</p>
      <p>Your current balance is ${money}</p>
      <div>
        <Link to="/deposit">Deposit</Link>
        <Link to="/transfer">Transfer</Link>
        <Link to="/accountinfo">Account Info</Link>
        <Link to="/PIN">Change PIN</Link>
      </div>
    </div>
  );
}

export default Dashboard;
