import axios from "axios";
import FormInput from "./FormInput";
import "./Registration.css"
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Registration = () => {
    // to reach the values object inside register boxes cuz its object ({})
    const [values, setValues] = useState({
        username:"",
        password:"",
        pin:"",
    })

    const navigate = useNavigate()

//inputs array for username, password, pin
    const inputs = [
        {
            id:1,
            name:"username",
            type:"text",
            placeholder:"Username",
            errorMessage:"Username should be 3-16 characters and shouldn't include any numbers or special character!",
            label:"Username",
            pattern: "^[A-Za-z]{3,16}$",
            required: true,
        },
        {
            id:2,
            name:"password",
            type:"text",
            placeholder:"Password",
            errorMessage:"Password should be 8-20 characters and must include 1 letter 1 number and 1 special character!",
            label:"Password",
            pattern: "^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[!@#$%^&*;])[A-Za-z0-9!@#$%^&*;]{8,20}$",
            required: true,
        },
        {
            id:3,
            name:"pin",
            type:"password",
            placeholder:"Pin",
            errorMessage:"Pin must be 4 digits",
            label:"Pin",
            pattern:"^[0-9]{4}$",
            required: true,
        }
    ]
  // Saving as FormData in congole log when submit 
    const handleSubmit = (e) => {
        e.preventDefault();

        if(!values.username){
            toast.error("Please enter a username...");
        }
        else if(!values.password){
            toast.error("Please enter a password...");
        }
        else if(!values.pin){
            toast.error("Please enter a PIN...");
        }
        else{
            if(values.username.length < 8 || values.username.length > 20){
                toast.error("Invalid Username")
            }
            else if(!/[A-Z]/.test(values.password)||!/\d/.test(values.password)||!/[!@#$%^&*;]/.test(values.password)){
                toast.error("Invalid password")
            }
            else if(values.pin.length != 4 || values.pin[0] != /\d/ || values.pin[1] != /\d/ || values.pin[2] != /\d/ || values.pin[3] != /\d/){
                toast.error("Invalid PIN")
            }
        }

  /*  const data = new FormData(e.target)
    console.log(Object.fromEntries(data.entries()))*/

    };
    const onChange = (e) => {
        setValues({...values, [e.target.name]: e.target.value });
    }
   
    const handleClick = async e =>{
        e.preventDefault();
        try {
            //what if our end point change
            await axios.post("http://localhost:5000/users", values)
            navigate("/")
        } catch (err) {
            console.log(err)
        }
    }
    console.log(values);
    return (
        <div className="register">
            <form onSubmit={handleSubmit}>
                <h1>Register</h1>
                {inputs.map((input) => (
                    <FormInput key={input.id} {...input} value={values.name} onChange={onChange}/>
                    ))}
                <button onClick={handleClick}>Submit</button>
            </form>
        </div>
        
    )
};
export default Registration
