          </Link>

          <Link to="/transfer">
            <button className="btn btn-transfer">Transfer Money</button>
          </Link>

        </div>
        <table>
          <tr>
            <th>Account Number:</th>
            <td>#{user.accnum}</td>

          </tr>
          <tr>
            <th>Balance:</th>
            <td>${user.money}</td>

          </tr>
        </table>
        {/* <div>
